.immersive-translate-input { position: absolute; inset: 0px; z-index: 2147483647; display: flex; justify-content: center; align-items: center; }.immersive-translate-input-loading { --loading-color: #f78fb6; width: 6px; height: 6px; border-radius: 50%; display: block; margin: 12px auto; position: relative; color: white; left: -100px; box-sizing: border-box; animation: 1.5s linear 0s infinite normal none running immersiveTranslateShadowRolling; }@keyframes immersiveTranslateShadowRolling { 
  0% { box-shadow: rgba(255, 255, 255, 0) 0px 0px, rgba(255, 255, 255, 0) 0px 0px, rgba(255, 255, 255, 0) 0px 0px, rgba(255, 255, 255, 0) 0px 0px; }
  12% { box-shadow: 100px 0 var(--loading-color), 0px 0 rgba(255, 255, 255, 0), 0px 0 rgba(255, 255, 255, 0), 0px 0 rgba(255, 255, 255, 0); }
  25% { box-shadow: 110px 0 var(--loading-color), 100px 0 var(--loading-color), 0px 0 rgba(255, 255, 255, 0), 0px 0 rgba(255, 255, 255, 0); }
  36% { box-shadow: 120px 0 var(--loading-color), 110px 0 var(--loading-color), 100px 0 var(--loading-color), 0px 0 rgba(255, 255, 255, 0); }
  50% { box-shadow: 130px 0 var(--loading-color), 120px 0 var(--loading-color), 110px 0 var(--loading-color), 100px 0 var(--loading-color); }
  62% { box-shadow: 200px 0 rgba(255, 255, 255, 0), 130px 0 var(--loading-color), 120px 0 var(--loading-color), 110px 0 var(--loading-color); }
  75% { box-shadow: 200px 0 rgba(255, 255, 255, 0), 200px 0 rgba(255, 255, 255, 0), 130px 0 var(--loading-color), 120px 0 var(--loading-color); }
  87% { box-shadow: 200px 0 rgba(255, 255, 255, 0), 200px 0 rgba(255, 255, 255, 0), 200px 0 rgba(255, 255, 255, 0), 130px 0 var(--loading-color); }
  100% { box-shadow: rgba(255, 255, 255, 0) 200px 0px, rgba(255, 255, 255, 0) 200px 0px, rgba(255, 255, 255, 0) 200px 0px, rgba(255, 255, 255, 0) 200px 0px; }
}.immersive-translate-search-recomend { border: 1px solid rgb(218, 220, 224); border-radius: 8px; padding: 16px; margin-bottom: 16px; position: relative; font-size: 16px; }.immersive-translate-search-enhancement-en-title { color: rgb(77, 81, 86); }@media (prefers-color-scheme: dark) {
  .immersive-translate-search-recomend { border: 1px solid rgb(60, 64, 67); }
  .immersive-translate-close-action svg { fill: rgb(189, 193, 198); }
  .immersive-translate-search-enhancement-en-title { color: rgb(189, 193, 198); }
}.immersive-translate-search-settings { position: absolute; top: 16px; right: 16px; cursor: pointer; }.immersive-translate-search-recomend::before { }.immersive-translate-search-title { }.immersive-translate-search-title-wrapper { }.immersive-translate-search-time { font-size: 12px; margin: 4px 0px 24px; color: rgb(112, 117, 122); }.immersive-translate-expand-items { display: none; }.immersive-translate-search-more { margin-top: 16px; font-size: 14px; }.immersive-translate-modal { display: none; position: fixed; z-index: 1000000; left: 0px; top: 0px; width: 100%; height: 100%; overflow: auto; background-color: rgba(0, 0, 0, 0.4); font-size: 15px; }.immersive-translate-modal-content { background-color: rgb(254, 254, 254); margin: 15% auto; padding: 20px; border: 1px solid rgb(136, 136, 136); border-radius: 10px; width: 80%; max-width: 500px; font-family: system-ui, -apple-system, "Segoe UI", Roboto, Ubuntu, Cantarell, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji"; }.immersive-translate-modal-title { font-size: 1.3rem; font-weight: 500; margin-bottom: 20px; color: rgb(65, 84, 98); }.immersive-translate-modal-body { color: rgb(65, 84, 98); }.immersive-translate-close { color: rgb(170, 170, 170); float: right; font-size: 28px; font-weight: bold; }.immersive-translate-close:hover, .immersive-translate-close:focus { color: black; text-decoration: none; cursor: pointer; }.immersive-translate-modal-footer { display: flex; justify-content: flex-end; flex-wrap: wrap; margin-top: 20px; }.immersive-translate-btn { width: fit-content; color: rgb(255, 255, 255); background-color: rgb(234, 76, 137); border: none; margin: 5px; padding: 10px 20px; font-size: 1rem; border-radius: 5px; display: flex; align-items: center; cursor: pointer; transition: background-color 0.3s ease 0s; }.immersive-translate-cancel-btn { background-color: rgb(89, 107, 120); }.immersive-translate-btn:hover { background-color: rgb(240, 130, 172); }.immersive-translate-cancel-btn:hover { background-color: rgb(65, 84, 98); }.immersive-translate-btn svg { margin-right: 5px; }.immersive-translate-link { cursor: pointer; user-select: none; -webkit-user-drag: none; text-decoration: none; color: rgb(0, 123, 255); -webkit-tap-highlight-color: rgba(0, 0, 0, 0.1); }.immersive-translate-primary-link { cursor: pointer; user-select: none; -webkit-user-drag: none; text-decoration: none; color: rgb(234, 76, 137); -webkit-tap-highlight-color: rgba(0, 0, 0, 0.1); }.immersive-translate-modal input[type="radio"] { margin: 0px 6px; cursor: pointer; }.immersive-translate-modal label { cursor: pointer; }.immersive-translate-close-action { position: absolute; top: 2px; right: 0px; cursor: pointer; }

